from django.db import models

#viaticos de combustible 

class TicketCombustible(models.Model):
    serial_id = models.CharField("Serial o ID", max_length=7, primary_key=True)
    numero_lote = models.CharField("NÃºmero de lote", max_length=13)
    nombre_bomba_gasolinera = models.CharField("Nombre de la bomba gasolinera", max_length=255)
    monto = models.IntegerField("Monto o valor")
    fecha_vencimiento = models.DateField("Fecha Vencimiento")


class SolicitudCombustible(models.Model):
    fecha_solicitud = models.DateField("Fecha Solicitud", auto_now=True)
    fecha_inicio_uso_combustible = models.DateField("Fecha Inicio Uso del Combustible", auto_now=False, auto_now_add=False)
    fecha_finalizacion_uso_combustible = models.DateField("Fecha Finalizacion Uso del Combustible", auto_now=False, auto_now_add=False)
    departamento_solicitante = models.ForeignKey("AdministracionSistema.Departamento", verbose_name="Departamento", on_delete=models.CASCADE)
    usuario_solicitante = models.CharField("Usuario Solicitante", max_length=255)
    motivo_solicitud = models.TextField("Motivo de Solicitud")
    resultados_esperados = models.TextField("Resultados Esperados")
    vehiculos_utilizados = models.TextField("Detalles de Vehiculos Utilizados")
    cantidad_pasajeros = models.IntegerField("Cantidad Pasajeros")
    distancia_ida_vuelta = models.IntegerField("Distancia a recorrer (km)")
    galones_gasolina = models.FloatField("Galones de gasolina")
    precio_galon_gasolina = models.FloatField("Precio Galon Gasolina")
    precio_peaje = models.FloatField("Total Peaje")
    comentarios = models.TextField("Comentarios u observaciones")