from django.contrib import admin
from .models import EntregaRaciones


class EntregaRacionesAdmin(admin.ModelAdmin):
    list_display = tuple([m.name for m in EntregaRaciones._meta.fields])
    list_filter = ("fecha_hora_creado", "periodo_escolar", "tanda_ce", "tipo_producto", "frecuencia_entrega", "condicion_raciones", "entrega_segun_menu", "provincia_ce", "municipio_ce", "regional_ce", "distrito_ce", "servicio_modalidad", )
    search_fields =  ("codigo_ce", "nombre_ce", "numero_conduce", "cedula_director", "nombre_director", "cedula_proveedor", "nombre_proveedor", )
    date_hierarchy = "fecha_hora_creado"
    using = 'entrega_raciones'
    def get_queryset(self, request):
        # Tell Django to look for objects on the 'other' database.
        return super().get_queryset(request).using(self.using)

# Register your models here.
admin.site.register(EntregaRaciones, EntregaRacionesAdmin)