from django.db import models
from datetime import datetime

# Create your models here.
class EntregaRaciones(models.Model):

    fecha_hora_creado =  models.DateTimeField("Fecha/Hora Creado")
    periodo_escolar = models.CharField("Periodo Escolar", max_length=50)
    numero_conduce = models.IntegerField("Numero de conduce", default=1)
    #datos del centro educativo

    codigo_ce = models.CharField("Código CE", max_length=5)
    nombre_ce = models.CharField("Nombre CE", max_length=255)
    provincia_ce = models.CharField("Provincia CE", max_length=255)
    municipio_ce = models.CharField("Provincia CE", max_length=255)
    regional_ce = models.CharField("Regional CE", max_length=2)
    distrito_ce = models.CharField("Distrito CE", max_length=4)
    sector_ce = models.CharField("Sector CE", max_length=255, null=True, blank=True)
    direccion_ce = models.CharField("Dirección CE", max_length=255, null=True, blank=True)
    telefono_ce = models.CharField("Teléfono CE", max_length=14, null=True, blank=True)
    servicio_modalidad = models.CharField("Servicio CE", max_length=255, null=True, blank=True)
    tanda_ce = models.CharField("Tanda CE", max_length=255, null=True, blank=True)

    #datos del director del ce

    cedula_director = models.CharField("Cédula Director", max_length=11, null=True, blank=True)
    nombre_director = models.CharField("Nombre Director", max_length=255, null=True, blank=True)
    telefono_director = models.CharField("Teléfono Director", max_length=14, null=True, blank=True)
    email_director = models.EmailField("Email Director", max_length=254, null=True, blank=True)

    #datos del analista de seguimiento al servicio de alimentacion

    nombre_analista_seguimiento_servicio = models.CharField("Nombre Analista", max_length=255, null=True, blank=True)
    cedula_analista_seguimiento_servicio = models.CharField("Cédula Analista", max_length=11, null=True, blank=True)
    telefono_analista_seguimiento_servicio = models.CharField("Teléfono Analista", max_length=14, null=True, blank=True)
    email_analista_seguimiento_servicio = models.EmailField("Email Analista", max_length=254, null=True, blank=True)
    cargo_analista_seguimiento_servicio = models.CharField("Cargo Analista", max_length=10, null=True, blank=True)

    #datos del proveedor

    cedula_proveedor = models.CharField("Cédula Proveedor", max_length=11)
    nombre_proveedor = models.CharField("Nombre Proveedor", max_length=255)
    telefono_proveedor = models.CharField("Teléfono Proveedor", max_length=14, null=True, blank=True)
    email_proveedor = models.EmailField("Email Proveedor", max_length=254, null=True, blank=True)

    #datos de la entrega de raciones

    tipo_producto = models.CharField("Tipo Producto/Alimentos", max_length=255)
    raciones_periodo = models.IntegerField("Raciones del periodo", null=True, blank=True)
    raciones_consumidas = models.IntegerField("Raciones Consumidas", null=True, blank=True)
    frecuencia_entrega = models.CharField("Frecuencia Entrega", max_length=70, choices=[("Diario", "Diario"), ("Semanal", "Semanal"), ("Cada dos semanas", "Cada dos semanas"), ("Mensual", "Mensual") ], default="Diario")
    raciones_a_recibir = models.IntegerField("Raciones a recibir")
    raciones_recibidas = models.IntegerField("Raciones recibidas")
    condicion_raciones = models.CharField("Condición de las raciones", max_length=50, choices=[("Buena", "Buena"), ("Regular", "Regular"), ("Mala", "Mala")])
    entrega_segun_menu = models.BooleanField("¿Entregó según el menú?")
    comentario = models.TextField("Comentario", null=True, blank=True) 


    def __str__(self):
        return self.codigo_ce + " - " + f"{self.id}"