from django.contrib import admin
from .models import Cita

# Register your models here.

class CitaAdmin(admin.ModelAdmin):
    list_display = ('fecha_cita', 'hora_cita', 'representante_estudiante', 'nombre_estudiante', 'edad_estudiante', 'sexo_estudiante', 'curso', 'nombre_escuela', 'distrito_escuela', 'motivo', 'comentarios')
    list_filter = ('fecha_cita', 'edad_estudiante', 'sexo_estudiante', 'curso', 'distrito_escuela')
    search_fields = ('representante_estudiante__nombre', 'representante_estudiante__cedula_rnc', 'nombre_estudiante', 'edad_estudiante', 'sexo_estudiante', 'curso', 'nombre_escuela', 'distrito_escuela')
    date_hierarchy = 'fecha_cita'
    autocomplete_fields = ('representante_estudiante',)


admin.site.register(Cita, CitaAdmin)