from django.db import models

# Create your models here.

class Cita(models.Model):
    fecha_registro = models.DateField("Fecha de registro", auto_now=True)
    fecha_cita = models.DateField("Fecha")
    hora_cita = models.TimeField("Hora")
    representante_estudiante = models.ForeignKey("Recepcion.PersonaAtendida", verbose_name= "Representante del estudiante", on_delete=models.CASCADE)
    nombre_estudiante = models.CharField("Nombre", max_length=255)
    edad_estudiante = models.IntegerField("Edad")
    sexo_estudiante = models.CharField("Sexo", max_length=10, choices=[('MASCULINO', 'MASCULINO'), ('FEMENINO', 'FEMENINO')])
    curso = models.CharField("Curso", max_length=100)
    nombre_escuela = models.CharField("Nombre Escuela", max_length=255)
    distrito_escuela = models.CharField("Distrito Escuela", max_length=4)
    motivo = models.TextField("Motivo de la cita", null=True, blank=True)
    comentarios = models.TextField("Comentarios", null=True, blank=True)
