from django.db import models
from django.contrib.auth.models import User
from AdministracionSistema.models import Departamento, Division, Empleado


# Create your models here.

class PersonaAtendida(models.Model):
    nombre = models.CharField("Nombre", max_length=255)
    departamento = models.ForeignKey(Departamento, verbose_name= "Departamento", on_delete=models.CASCADE)
    division = models.ForeignKey(Division, verbose_name= "Division", on_delete=models.CASCADE, null=True, blank=True)

    def __str__(self):
        return self.nombre

class LLamada(models.Model):
    fecha_registro = models.DateField("Fecha registro", auto_now=True)
    persona_atendida = models.ForeignKey(PersonaAtendida, verbose_name="Persona Atendida", on_delete=models.CASCADE)
    motivo = models.TextField("Motivo", null=True, blank=True)

    def __str__(self):
        return self.persona_atendida.nombre


class Ticket(models.Model):
    fecha_registro = models.DateField("Fecha registro", auto_now=True)
    estado = models.CharField("Estado", max_length=100, choices=[('COMPLETADO', 'COMPLETADO'), ('EN ESPERA DE APROBACION', 'EN ESPERA DE APROBACION'), ('EN ESPERA POR EL USUARIO', 'EN ESPERA POR EL USUARIO'), ('EN PROCESO', 'EN PROCESO'), ('LISTA DE ESPERA', 'LISTA DE ESPERA'), ('CANCELADO', 'CANCELADO')])
    persona_asignada = models.ForeignKey(User, verbose_name= "Asignado a", on_delete=models.CASCADE, null=True, blank=True, related_name='+')
    cargo_persona_asignada = models.CharField("Cargo", max_length=255, null=True, blank=True)
    transferido_por = models.ForeignKey(User, verbose_name= "Transferido por", on_delete=models.CASCADE, null=True, blank=True, related_name='+')
    motivo_del_requerimiento = models.TextField("Motivo del requerimiento", null=True, blank=True)
    prioridad = models.CharField("Prioridad", max_length=50, null=True, blank=True, choices=[('BAJA', 'BAJA'), ('NORMAL', 'NORMAL'), ('ALTA', 'ALTA')])
    departamento_solicitador = models.ForeignKey(Departamento, verbose_name= "Departamento Solicitador", on_delete=models.CASCADE, null=True, blank=True)

    def save(self):

        self.cargo_persona_asignada = self.persona_asignada.empleado.cargo
        return super().save()
    
